mapper.select:
SELECT *
FROM question_with_type_subject_knowledge
WHERE question_id != #{questionId}
  AND question_difficulty BETWEEN #{difficultyLower} AND #{difficultyUpper}
  AND type_id = #{typeId}
  AND knowledge_id = #{knowledgeId}
ORDER BY ABS(question_difficulty - #{questionDifficulty})

SELECT *
FROM question_with_type_subject_knowledge
WHERE question_id != #{questionId}
  AND type_id = #{typeId}
  AND knowledge_id = #{knowledgeId}
ORDER BY ABS(question_difficulty - #{questionDifficulty})
